document.addEventListener("DOMContentLoaded",(()=>{document.querySelectorAll("[data-popup-trigger]").forEach((e=>{e.addEventListener("click",(()=>{const t=e.getAttribute("data-popup-trigger"),o=document.querySelector(`[data-popup="${t}"]`);o&&o.classList.add("show"),console.log(t)}))})),document.querySelectorAll("[data-popup]").forEach((e=>{e.querySelector(".popup-close").addEventListener("click",(()=>{e.classList.remove("show")})),e.addEventListener("click",(t=>{t.target===e&&e.classList.remove("show")}))}))}));