const slider=document.querySelector(".slider"),track=slider.querySelector(".slider__track"),slides=Array.from(slider.querySelectorAll(".slider__slide")),btnPrev=slider.querySelector(".slider__btn-prev"),btnNext=slider.querySelector(".slider__btn-next"),pagination=slider.querySelector(".slider__pagination");let currentIndex=0;slides.forEach(((e,t)=>{const n=document.createElement("li");n.classList.add("slider__pagination-item"),t===currentIndex&&n.classList.add("active"),pagination.appendChild(n),n.addEventListener("click",(()=>{currentIndex=t,updateSliderPosition(),updatePagination()}))}));const dots=Array.from(pagination.querySelectorAll(".slider__pagination-item"));function updateSliderPosition(){const e=100*-currentIndex;track.style.transform=`translateX(${e}%)`}function updatePagination(){dots.forEach(((e,t)=>{e.classList.toggle("active",t===currentIndex)}))}btnPrev.addEventListener("click",(()=>{currentIndex=currentIndex>0?currentIndex-1:slides.length-1,updateSliderPosition(),updatePagination()})),btnNext.addEventListener("click",(()=>{currentIndex=currentIndex<slides.length-1?currentIndex+1:0,updateSliderPosition(),updatePagination()})),setInterval((()=>{btnNext.click()}),5e3);